import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate, useNavigate } from 'react-router-dom';

function CoverLetterSection({
    resumeText,
    jobDescription,
    selectedSections,
    setSelectedSections,
}) {
    const [options, setOptions] = useState([]);
    const [previousOptions, setPreviousOptions] = useState([]);
    const [showNewOptions, setShowNewOptions] = useState(false);
    const [currentSection, setCurrentSection] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [selectedOption, setSelectedOption] = useState('');
    const [currentStep, setCurrentStep] = useState(0);
    const navigate = useNavigate();

    const steps = [
        { 
            name: 'Open Hook', 
            endpoint: '/generate-open-hook',
            description: 'Create a compelling introduction that grabs attention'
        },
        { 
            name: 'Key Experiences', 
            endpoint: '/generate-key-experiences',
            description: 'Highlight your most relevant achievements'
        },
        { 
            name: 'Personal Values', 
            endpoint: '/generate-personal-values',
            description: 'Connect your values with the company culture'
        },
        { 
            name: 'Closing Statement', 
            endpoint: '/generate-closing-statement',
            description: 'End with a strong call to action'
        },
    ];

    useEffect(() => {
        console.log('Current selectedSections state:', selectedSections);
    }, [selectedSections]);

    useEffect(() => {
        if (resumeText && jobDescription) {
            generateSection();
        }
    }, [currentStep, resumeText, jobDescription]);

    if (!resumeText || !jobDescription) {
        alert('Please upload your resume and enter the job description before proceeding.');
        return <Navigate to="/" replace />;
    }

    const generateSection = async () => {
        const current = steps[currentStep];
        setLoading(true);
        setError('');
        setCurrentSection(current.name);
        setShowNewOptions(false);
        console.log(`Generating options for section: ${current.name}`);

        try {
            const response = await axios.post(`http://localhost:3000${current.endpoint}`, {
                jobDescription,
                resumeText,
            });

            if (response.data && Array.isArray(response.data.options)) {
                setOptions(response.data.options);
                setPreviousOptions([]);
                setError('');
            } else {
                throw new Error('Invalid response data');
            }
        } catch (error) {
            console.error('Error generating section:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSelection = (optionText) => {
        setSelectedOption(optionText);
        setError('');
        console.log(`Selected option for ${currentSection}:`, optionText);
    };

    const handleRegenerateOptions = async () => {
        setPreviousOptions(options);
        setShowNewOptions(true);
        const current = steps[currentStep];
        setLoading(true);

        try {
            const response = await axios.post(`http://localhost:3000${current.endpoint}`, {
                jobDescription,
                resumeText,
            });

            if (response.data && Array.isArray(response.data.options)) {
                setOptions(response.data.options);
            }
        } catch (error) {
            console.error('Error regenerating options:', error);
        } finally {
            setLoading(false);
        }
    };

    const saveSelection = () => {
        if (!selectedOption) {
            setError('Please select an option before proceeding.');
            return;
        }

        const cleanedOption = selectedOption.replace(/^Option \d+:\s*/, '');

        setSelectedSections((prevSections) => ({
            ...prevSections,
            [currentSection]: cleanedOption,
        }));
    };

    const handleNextStep = () => {
        saveSelection();
        if (currentStep < steps.length - 1) {
            setCurrentStep((prevStep) => prevStep + 1);
        } else {
            navigate('/download');
        }
    };

    return (
        <div className="w-4/5 mx-auto mt-10 p-8 bg-white shadow-xl rounded-lg">
            {/* Enhanced Progress Steps */}
            <div className="flex justify-center mb-10 space-x-4">
                {steps.map((step, index) => (
                    <div key={index} className="flex flex-col items-center">
                        <div
                            className={`w-12 h-12 flex items-center justify-center rounded-full text-lg font-bold transition-all duration-300
                                ${index <= currentStep
                                    ? 'bg-gray-800 text-white hover:bg-blue-500 transform hover:scale-110'
                                    : 'bg-gray-300 text-gray-500'
                                }`}
                        >
                            {index + 1}
                        </div>
                        <span className="mt-2 text-sm font-medium text-gray-600">
                            {step.name}
                        </span>
                    </div>
                ))}
            </div>

            {/* Current Section Header */}
            <div className="text-center mb-8">
                <h2 className={`text-2xl font-bold mb-2 ${currentStep === 0 ? 'text-blue-600 transform scale-110' : 'text-gray-800'}`}>
                    {steps[currentStep].name}
                </h2>
                <p className="text-gray-600">{steps[currentStep].description}</p>
            </div>

            {error && (
                <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4">
                    {error}
                </div>
            )}

            {loading ? (
                <div className="flex justify-center items-center p-8">
                    <svg
                        className="animate-spin h-8 w-8 text-blue-500"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                    >
                        <circle
                            className="opacity-25"
                            cx="12"
                            cy="12"
                            r="10"
                            stroke="currentColor"
                            strokeWidth="4"
                        ></circle>
                        <path
                            className="opacity-75"
                            fill="currentColor"
                            d="M4 12a8 8 0 018-8v8H4z"
                        ></path>
                    </svg>
                    <p className="text-blue-500 ml-3 text-lg">Generating options...</p>
                </div>
            ) : (
                <div className="space-y-6">
                    {/* Main Options Display */}
                    <div className="max-h-[500px] overflow-y-auto mb-4 border p-6 rounded-lg bg-gray-50">
                        <h4 className="text-xl font-semibold mb-4">Choose an option:</h4>
                        {options.map((item, index) => (
                            <div
                                key={index}
                                className={`mb-4 p-4 rounded-lg cursor-pointer transition-all duration-200 
                                    ${selectedOption === item.option
                                        ? 'bg-yellow-100 border-2 border-yellow-400 shadow-md'
                                        : 'bg-white border border-gray-200 hover:border-blue-300 hover:shadow-md'
                                    }`}
                                onClick={() => handleSelection(item.option)}
                            >
                                <div className="flex items-center">
                                    <input
                                        type="radio"
                                        name="sectionOption"
                                        value={item.option}
                                        checked={selectedOption === item.option}
                                        onChange={() => handleSelection(item.option)}
                                        className="w-4 h-4 text-blue-600"
                                    />
                                    <span className="ml-3 font-semibold text-lg">Option {index + 1}</span>
                                </div>
                                <p className="mt-3 text-gray-700">{item.option}</p>
                                <div className="mt-3 bg-gray-50 p-3 rounded">
                                    <p className="text-sm text-gray-600 italic">
                                        <span className="font-medium">Why Choose This:</span> {item.reason}
                                    </p>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Previous Options Display (if available) */}
                    {showNewOptions && previousOptions.length > 0 && (
                        <div className="border p-4 rounded-lg bg-gray-100 mt-4">
                            <h5 className="text-lg font-semibold mb-3">Previous Options:</h5>
                            <div className="space-y-3">
                                {previousOptions.map((item, index) => (
                                    <div key={index} className="p-3 bg-white rounded shadow-sm">
                                        <p className="font-medium">Option {index + 1}:</p>
                                        <p className="text-sm text-gray-600 mt-1">{item.option}</p>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {/* Actions */}
                    <div className="flex justify-between items-center mt-6">
                        <button
                            onClick={handleRegenerateOptions}
                            disabled={loading}
                            className="px-6 py-3 bg-gray-800 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200"
                        >
                            See Other Options
                        </button>

                        <button
                            onClick={handleNextStep}
                            disabled={!selectedOption || loading}
                            className={`px-8 py-3 rounded-lg font-semibold transition-all duration-200
                                ${selectedOption && !loading
                                    ? 'bg-gray-900 text-white hover:bg-blue-500 transform hover:scale-105'
                                    : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                }`}
                        >
                            {currentStep < steps.length - 1 ? 'Save and Continue' : 'Generate Cover Letter'}
                        </button>
                    </div>

                    <p className="text-sm text-gray-600 text-center mt-4">
                        Step {currentStep + 1} of {steps.length}
                    </p>
                </div>
            )}
        </div>
    );
}

export default CoverLetterSection;
