import React from 'react';
import { Link } from 'react-router-dom';

// 1. HeroSection Component
const HeroSection = () => (
  <section className="bg-gray-900 text-white py-20">
    <div className="container mx-auto px-6 text-center">
      <h1 className="text-5xl font-bold mb-4">Create the Perfect Cover Letter in Minutes!</h1>
      <p className="text-xl mb-8">
        Tailored to Your Resume. Aligned with Any Job Description. Made for Students and Professionals.
      </p>
      <div className="flex justify-center space-x-4">
        <Link to="/upload-resume">
          <button className="bg-white text-blue-500 font-semibold py-3 px-6 rounded-full hover:bg-gray-100 transition-colors duration-200">
            Get Started for Free!
          </button>
        </Link>
        <a href="#how-it-works">
          <button className="bg-transparent border border-white font-semibold py-3 px-6 rounded-full hover:bg-blue-500 transition-colors duration-200">
            See How It Works
          </button>
        </a>
      </div>
    </div>
  </section>
);

// 2. IntroSection Component
const IntroSection = () => (
  <section className="py-16">
    <div className="container mx-auto px-6 text-center">
      <h2 className="text-4xl font-bold mb-4">What is the Cover Letter Generator?</h2>
      <p className="text-lg text-gray-700 mb-8 max-w-3xl mx-auto">
        Our Cover Letter Generator is a smart tool designed to help job seekers, especially final-year students and new graduates, craft tailored and impactful cover letters that stand out to employers. By analyzing your resume and the job description, it generates personalized content for every section of your cover letter.
      </p>
      {/* Replace 'app-screenshot.png' with your image path */}
      <img src="app-screenshot.png" alt="Screenshot of Cover Letter Generator Application" className="mx-auto rounded-lg shadow-lg" />
    </div>
  </section>
);

// 3. ScrollingIconSection Component
const ScrollingIconSection = () => (
  <section className="bg-purple-100 py-16">
    <div className="container mx-auto px-6">
      <div className="flex flex-col lg:flex-row items-center justify-between">

        {/* Left Column: Text and Scrolling Icons */}
        <div className="w-full lg:w-1/2 mb-12 lg:mb-0">
          <h2 className="text-3xl font-bold mb-4">
            No matter where you connect with people...
          </h2>
          <div className="relative overflow-hidden w-full mt-6">
            <div
              className="flex items-center space-x-6 animate-scroll"
            >
              {/* Original Icons */}
              {[
                "ðŸ“„", // Resume
                "ðŸ’¼", // LinkedIn
                "âœ‰ï¸", // Email
                "ðŸ“§", // Gmail
                "ðŸ–‡ï¸", // ATS
                "ðŸ”—", // Connection
                "ðŸ“", // File
              ].map((icon, index) => (
                <div
                  key={index}
                  className="flex-shrink-0 w-16 h-16 bg-white rounded-full flex items-center justify-center shadow-lg"
                >
                  <span className="text-2xl">{icon}</span>
                </div>
              ))}
              {/* Duplicate Icons for Seamless Scrolling */}
              {[
                "ðŸ“„",
                "ðŸ’¼",
                "âœ‰ï¸",
                "ðŸ“§",
                "ðŸ–‡ï¸",
                "ðŸ”—",
                "ðŸ“",
              ].map((icon, index) => (
                <div
                  key={`dup-${index}`}
                  className="flex-shrink-0 w-16 h-16 bg-white rounded-full flex items-center justify-center shadow-lg"
                >
                  <span className="text-2xl">{icon}</span>
                </div>
              ))}
              {/* Custom Icon */}
              <div className="flex-shrink-0 w-16 h-16 bg-white rounded-full flex items-center justify-center shadow-lg">
                {/* Placeholder Image */}
                <img
                  src="https://via.placeholder.com/40"
                  alt="Custom Icon"
                  className="w-10 h-10"
                />
              </div>
              {/* Duplicate Custom Icon for Seamless Scrolling */}
              <div className="flex-shrink-0 w-16 h-16 bg-white rounded-full flex items-center justify-center shadow-lg">
                <img
                  src="https://via.placeholder.com/40"
                  alt="Custom Icon"
                  className="w-10 h-10"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Right Column: Additional Text */}
        <div className="w-full lg:w-1/2">
          <p className="text-lg text-gray-700">
            ...always stay organized with our Cover Letter Generator.
          </p>
        </div>
      </div>
    </div>

    {/* Keyframe Animation */}
    <style>
      {`
        @keyframes scrollIcons {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
        .animate-scroll {
          display: flex;
          animation: scrollIcons 20s linear infinite;
        }
        @media (prefers-reduced-motion: reduce) {
          .animate-scroll {
            animation: none;
          }
        }
      `}
    </style>
  </section>
); 

// 4. ReasonSection Component
const ReasonSection = () => (
  <section className="bg-gray-100 py-16">
    <div className="container mx-auto px-6">
      <h2 className="text-4xl font-bold text-center mb-12">Why Use the Cover Letter Generator?</h2>
      <div className="flex flex-wrap -mx-4">
        {/* Benefit Cards */}
        {[
          { icon: 'â±ï¸', title: 'Time-Saving', description: 'No more starting from scratch. Create a polished cover letter in minutes.' },
          { icon: 'ðŸŽ¯', title: 'Personalized', description: 'Tailors content to align with the job description and highlights your unique skills.' },
          { icon: 'ðŸ“', title: 'Guided Process', description: 'Step-by-step structure ensures you never miss an important section.' },
          { icon: 'âœ¨', title: 'Customizable Options', description: 'Five content suggestions for each section, empowering you to choose what suits you best.' },
          { icon: 'ðŸŽ“', title: 'Designed for Students & Grads', description: 'Created with new professionals in mind, focusing on ease of use.' },
        ].map((benefit, index) => (
          <div key={index} className="w-full md:w-1/2 lg:w-1/3 px-4 mb-8">
            <div className="bg-white p-6 rounded-lg shadow text-center hover:shadow-lg transition-shadow duration-200">
              <div className="text-4xl mb-4">{benefit.icon}</div>
              <h3 className="font-semibold text-xl mb-2">{benefit.title}</h3>
              <p className="text-gray-700">{benefit.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

// 5. GuideSection Component
const GuideSection = () => (
  <section id="how-it-works" className="py-16">
    <div className="container mx-auto px-6 text-center">
      <h2 className="text-4xl font-bold mb-12">How Does It Work?</h2>
      <div className="flex flex-wrap -mx-4">
        {/* Steps */}
        {[
          { icon: 'ðŸ“„', title: 'Upload Your Resume', description: 'Simply upload your resume and paste the job description.' },
          { icon: 'ðŸ”', title: 'Get a Skill Match', description: 'View a detailed comparison table to identify key alignments.' },
          { icon: 'âœï¸', title: 'Customize Your Cover Letter', description: 'Select personalized options for each sectionâ€”Open Hook, Key Experiences, Closing Statement, and more.' },
          { icon: 'ðŸ“¥', title: 'Download or Copy', description: 'Get a professional, ready-to-use cover letter that you can edit further.' },
        ].map((step, index) => (
          <div key={index} className="w-full md:w-1/2 lg:w-1/4 px-4 mb-8">
            <div className="p-6 hover:bg-gray-100 rounded-lg transition-colors duration-200">
              <div className="text-5xl mb-4">{step.icon}</div>
              <h3 className="font-semibold text-xl mb-2">{step.title}</h3>
              <p className="text-gray-700">{step.description}</p>
            </div>
          </div>
        ))}
      </div>
      <Link to="/upload-resume">
        <button className="mt-8 bg-gray-900 text-white font-semibold py-3 px-6 rounded-full hover:bg-blue-500 transition-colors duration-200">
          Try It Now!
        </button>
      </Link>
    </div>
  </section>
);

// 6. FeaturesHighlight Component
const FeaturesHighlight = () => (
  <section className="py-16">
    <div className="container mx-auto px-6">
      <h2 className="text-4xl font-bold text-center mb-12">Features Youâ€™ll Love</h2>
      <div className="flex flex-wrap -mx-4">
        {/* Feature Cards */}
        {[
          { image: 'feature1.png', title: 'Skill Alignment Table', description: 'Shows how your resume aligns with the job description.' },
          { image: 'feature2.png', title: 'Five Suggestions per Section', description: 'Choose from multiple tailored options for each part of the letter.' },
          { image: 'feature3.png', title: 'In-Place Editing', description: 'Edit your cover letter directly in the tool before downloading.' },
          { image: 'feature4.png', title: 'Customizable Format', description: 'Download as Word or copy to your clipboard.' },
        ].map((feature, index) => (
          <div key={index} className="w-full md:w-1/2 lg:w-1/4 px-4 mb-8">
            <div className="text-center">
              {/* Replace 'featureX.png' with your image paths */}
              <img src={feature.image} alt={feature.title} className="mx-auto mb-4 h-32 w-32 object-contain" />
              <h3 className="font-semibold text-xl mb-2">{feature.title}</h3>
              <p className="text-gray-700">{feature.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

// 7. ComparisonTable Component
const ComparisonTable = () => (
  <section className="bg-gray-100 py-16">
    <div className="container mx-auto px-6">
      <h2 className="text-4xl font-bold text-center mb-12">Why Choose Our Tool?</h2>
      <div className="overflow-x-auto">
        <table className="w-full text-left border-collapse">
          <thead>
            <tr>
              <th className="px-4 py-2 border-b-2"></th>
              <th className="px-4 py-2 border-b-2">Our Tool</th>
              <th className="px-4 py-2 border-b-2">Generic Tools</th>
            </tr>
          </thead>
          <tbody>
            {[
              { feature: 'Skill Alignment Analysis', ourTool: 'âœ…', genericTools: 'âŒ' },
              { feature: 'Structured Guidance', ourTool: 'âœ…', genericTools: 'âš ï¸ Limited' },
              { feature: 'Personalized Content', ourTool: 'âœ…', genericTools: 'âŒ' },
              { feature: 'Time to Generate', ourTool: 'â±ï¸ 5 Minutes', genericTools: 'â±ï¸ 30+ Minutes' },
            ].map((row, index) => (
              <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                <td className="border px-4 py-2">{row.feature}</td>
                <td className="border px-4 py-2">{row.ourTool}</td>
                <td className="border px-4 py-2">{row.genericTools}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  </section>
);

// 8. FinalCTA Component
const FinalCTA = () => (
  <section className="bg-gray-600 text-white py-16">
    <div className="container mx-auto px-6 text-center">
      <h2 className="text-4xl font-bold mb-4">Ready to Land Your Dream Job?</h2>
      <p className="text-xl mb-8">No credit card required. Start creating your personalized cover letter in minutes.</p>
      <Link to="/upload-resume">
        <button className="bg-gray-900 text-white font-semibold py-3 px-6 rounded-full hover:bg-blue-500 transition-colors duration-200">
          Get Started Now!
        </button>
      </Link>
    </div>
  </section>
);

// 9. Footer Component
const Footer = () => (
  <footer className="bg-gray-800 text-white py-8">
    <div className="container mx-auto px-6">
      <div className="flex flex-col md:flex-row md:justify-between">
        <div className="mb-6 md:mb-0">
          <h3 className="font-bold text-lg mb-2">Cover Letter Generator</h3>
          <p className="text-gray-400">Helping you create the perfect cover letter.</p>
        </div>
        <div className="mb-6 md:mb-0">
          <h4 className="font-semibold mb-2">Quick Links</h4>
          <ul>
            <li><Link to="/about" className="text-gray-400 hover:text-white">About Us</Link></li>
            <li><Link to="/faq" className="text-gray-400 hover:text-white">FAQ</Link></li>
            <li><Link to="/contact" className="text-gray-400 hover:text-white">Contact Support</Link></li>
            <li><Link to="/privacy" className="text-gray-400 hover:text-white">Privacy Policy</Link></li>
          </ul>
        </div>
        <div>
          <h4 className="font-semibold mb-2">Follow Us</h4>
          <div className="flex space-x-4">
            {/* Replace with actual social media links and SVG icons */}
            <a href="https://twitter.com/yourprofile" target="_blank" rel="noopener noreferrer">
              <img src="twitter-icon.png" alt="Twitter" className="h-6 w-6" />
            </a>
            <a href="https://linkedin.com/in/yourprofile" target="_blank" rel="noopener noreferrer">
              <img src="linkedin-icon.png" alt="LinkedIn" className="h-6 w-6" />
            </a>
            <a href="https://github.com/yourprofile" target="_blank" rel="noopener noreferrer">
              <img src="github-icon.png" alt="GitHub" className="h-6 w-6" />
            </a>
          </div>
        </div>
      </div>
    </div>
  </footer>
);

// 10. LandingPage Component
function LandingPage() {
  return (
    <div className="bg-white font-sans">
      {/* Hero Section */}
      <HeroSection />

      {/* Introduction Section */}
      <IntroSection />

      {/* Scrolling Icon Section */}
      <ScrollingIconSection />

      {/* Reason Section */}
      <ReasonSection />

      {/* Guide Section */}
      <GuideSection />

      {/* Features Highlight */}
      <FeaturesHighlight />

      {/* Comparison Table */}
      <ComparisonTable />

      {/* Final Call-to-Action */}
      <FinalCTA />

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default LandingPage;
