import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate, useNavigate } from 'react-router-dom';

function CoverLetterSection({
    resumeText,
    jobDescription,
    selectedSections,
    setSelectedSections,
}) {
    // Hooks at the top level
    const [options, setOptions] = useState([]);
    const [previousOptions, setPreviousOptions] = useState([]);
    const [showNewOptions, setShowNewOptions] = useState(false);
    const [currentSection, setCurrentSection] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [selectedOption, setSelectedOption] = useState('');
    const [currentStep, setCurrentStep] = useState(0);
    const navigate = useNavigate();

    const steps = [
        { name: 'Open Hook', endpoint: '/generate-open-hook', description: 'Create a compelling introduction that grabs attention'},
        { name: 'Key Experiences', endpoint: '/generate-key-experiences',  description: 'Highlight your most relevant achievements'},
        { name: 'Personal Values', endpoint: '/generate-personal-values', description: 'Connect your values with the company culture'},
        { name: 'Closing Statement', endpoint: '/generate-closing-statement', description: 'End with a strong call to action' },
    ];

    //const estimatedLoadingTime = 5;

    useEffect(() => {
        console.log('Current selectedSections state:', selectedSections);
    }, [selectedSections]);

    useEffect(() => {
        if (resumeText && jobDescription) {
            generateSection();
        }
    }, [currentStep, resumeText, jobDescription]);

    if (!resumeText || !jobDescription) {
        alert('Please upload your resume and enter the job description before proceeding.');
        return <Navigate to="/" replace />;
    }

    const generateSection = async () => {
        const current = steps[currentStep];
        setLoading(true);
        setError('');
        setCurrentSection(current.name);
        setSelectedOption('');
        console.log(`Generating options for section: ${current.name}`);

        try {
            const response = await axios.post(`http://localhost:3000${current.endpoint}`, {
                jobDescription,
                resumeText,
            });

            if (response.data && Array.isArray(response.data.options)) {
                setOptions(response.data.options);
                setError('');
            } else {
                throw new Error('Invalid response data');
            }
        } catch (error) {
            console.error('Error generating section:', error);
            //setError('Failed to generate section. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleSelection = (optionText) => {
        setSelectedOption(optionText);
        setError('');
        console.log(`Selected option for ${currentSection}:`, optionText);
    };

    const saveSelection = () => {
        if (!selectedOption) {
            setError('Please select an option before proceeding.');
            return;
        }

        const cleanedOption = selectedOption.replace(/^Option \d+:\s*/, '');

        setSelectedSections((prevSections) => {
            const updatedSections = {
                ...prevSections,
                [currentSection]: cleanedOption,
            };
            console.log('Updated selectedSections state:', updatedSections);
            return updatedSections;
        });
    };

    const handleNextStep = () => {
        saveSelection();
        if (currentStep < steps.length - 1) {
            setCurrentStep((prevStep) => prevStep + 1);
        } else {
            navigate('/download');
        }
    };

    const handleRegenerateOptions = () => {
        setSelectedOption(''); // Clear any previous selection
        generateSection();     // Fetch new options
    };

    return (
        <div className="w-3/4 mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg">
            <div className="flex justify-center mb-8 space-x-2">
                {steps.map((step, index) => (
                    <div
                        key={index}
                        className={`w-8 h-8 flex items-center justify-center rounded-full mx-1 
                            ${
                                index <= currentStep
                                    ? 'bg-gray-800 text-white hover:bg-blue-400 hover:scale-105'
                                    : 'bg-gray-300 text-gray-400 opacity-50'
                            }`}
                    >
                        {index + 1}
                    </div>
                ))}
            </div>
            <h3 className="text-xl font-semibold mb-4 text-blue-500 text-center">
                {steps[currentStep].name}
            </h3>
            {error && <p className="text-red-500 text-center mb-4">{error}</p>}

            {loading ? (
                <div className="flex justify-center items-center">
                    <svg
                        className="animate-spin h-6 w-6 text-blue-500"
                        xmlns="http://www.w3.org/2000/svg"
                        fill="none"
                        viewBox="0 0 24 24"
                    >
                        <circle
                            className="opacity-25"
                            cx="12"
                            cy="12"
                            r="10"
                            stroke="currentColor"
                            strokeWidth="4"
                        ></circle>
                        <path
                            className="opacity-75"
                            fill="currentColor"
                            d="M4 12a8 8 0 018-8v8H4z"
                        ></path>
                    </svg>
                    <p className="text-blue-500 ml-2">Estimated loading time: 5-10 seconds...</p>
                </div>
            ) : (
                <>
                    <div className="max-h-[500px] overflow-y-auto mb-4 border p-4 rounded-lg">
                        <h4 className="text-lg font-medium mb-2">Choose an option:</h4>
                        <p className="mb-3 text-gray-700">
                            Choose the option that best fits your needs. Each option includes a ‘Why Choose This’ explanation to help you make the best choice
                        </p>
                        {options.map((item, index) => (
                            <div
                                key={index}
                                className={`mb-3 p-3 rounded-lg cursor-pointer transition-all duration-150 ease-in-out 
                                    ${
                                        selectedOption ===item.option
                                            ? 'bg-yellow-200 border border-yellow-400'
                                            : 'hover:bg-gray-100'
                                    }`}
                                onClick={() => handleSelection(item.option)}
                            >
                                <input
                                    type="radio"
                                    name="sectionOption"
                                    value={item.option}
                                    checked={selectedOption === item.option}
                                    onChange={() => handleSelection(item.option)}
                                    className="mr-2"
                                />
                                <label className="font-semibold">Option {index + 1}:</label>
                                <p className="mt-2">{item.option}</p>
                                <hr className="my-2 border-gray-300" />
                                <p className="mt-2 text-sm text-gray-600 italic">Why Choose This: {item.reason}</p>
                            </div>
                        ))}
                    </div>
                    {/* Regenerate Options Button */}
                    <button
                        onClick={handleRegenerateOptions}
                        disabled={loading}
                        className="mt-4 px-4 py-2 bg-gray-800 text-white rounded hover:bg-blue-400"
                    >
                        See Other Options
                    </button>
                </>
            )}

            <button
                onClick={handleNextStep}
                disabled={!selectedOption || loading}
                className={`mt-6 px-6 py-3 w-full font-semibold rounded-lg 
                    ${
                        selectedOption && !loading
                            ? 'bg-gray-900 text-white hover:bg-blue-500'
                            : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                    }`}
            >
                {currentStep < steps.length - 1
                    ? 'Save and Continue'
                    : 'Proceed to Generate your Cover Letter'}
            </button>

            <p className="text-sm text-gray-600 mt-4 text-center">
                Step {currentStep + 1} of {steps.length}
            </p>
        </div>
    );
}

export default CoverLetterSection;
