import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './DownloadCoverLetter.css';
import { htmlToText } from 'html-to-text';
import axios from 'axios';
import { Navigate, useNavigate } from 'react-router-dom';

function DownloadCoverLetter({ selectedSections, coverLetter, setCoverLetter, resumeText }) {
  const navigate = useNavigate();
  const [content, setContent] = useState('');
  const [personalDetails, setPersonalDetails] = useState({
    name: '',
    address: '',
    cityStateZip: '',
    email: '',
    phoneNumber: '',
  });
  const currentDate = new Date().toLocaleDateString();
  const requiredSections = ['Open Hook', 'Key Experiences', 'Personal Values', 'Closing Statement'];
  const allSectionsCompleted = requiredSections.every(section => selectedSections[section]);
  const [isDownloading, setIsDownloading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  // ... (keep existing useEffect and other utility functions)

  const handleDownload = async () => {
    setIsDownloading(true);
    setErrorMessage('');
    try {
      const response = await axios.post(
        'http://localhost:3000/generate-word',
        { coverLetter: content },
        { responseType: 'blob' }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'CoverLetter.docx');
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);

      setShowSuccessMessage(true);
      setTimeout(() => setShowSuccessMessage(false), 3000);
    } catch (error) {
      console.error('Error downloading Word document:', error);
      setErrorMessage('Failed to download the document. Please try again.');
    } finally {
      setIsDownloading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-4xl mx-auto">
        {/* Navigation Header */}
        <div className="flex justify-between items-center mb-8 px-6">
          <button
            onClick={() => navigate('/')}
            className="flex items-center text-gray-600 hover:text-blue-600 transition-colors"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            Return to Home
          </button>

          <div className="text-right">
            <h1 className="text-2xl font-bold text-gray-900">Final Step</h1>
            <p className="text-sm text-gray-600">Review and Download your Cover Letter</p>
          </div>
        </div>

        <div className="bg-white rounded-xl shadow-lg p-8">
          {/* Success Message */}
          {showSuccessMessage && (
            <div className="mb-6 p-4 bg-green-100 border border-green-400 text-green-700 rounded-lg">
              Cover letter downloaded successfully! You can now use it for your job application.
            </div>
          )}

          {/* Personal Details Section */}
          <div className="mb-8">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">Personal Information</h2>
            <div className="grid gap-4 md:grid-cols-2">
              {/* ... (keep existing personal details inputs) */}
            </div>
          </div>

          {/* Cover Letter Content */}
          <div className="mb-8">
            <h2 className="text-xl font-semibold mb-4 text-gray-800">Cover Letter Preview</h2>
            <div className="border rounded-lg p-4 bg-gray-50">
              <ReactQuill
                value={content}
                onChange={handleContentChange}
                modules={{ toolbar: toolbarOptions }}
                className="bg-white rounded-lg"
              />
            </div>
          </div>

          {/* Error Message */}
          {errorMessage && (
            <div className="mb-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg">
              {errorMessage}
            </div>
          )}

          {/* Download Section */}
          <div className="text-center">
            <button
              onClick={handleDownload}
              disabled={isDownloading}
              className={`
                w-full max-w-md
                py-4 px-8
                text-lg font-semibold
                text-white
                bg-blue-600 hover:bg-blue-700
                rounded-xl
                shadow-lg hover:shadow-xl
                transform transition-all duration-200 hover:-translate-y-1
                ${isDownloading ? 'opacity-50 cursor-not-allowed' : ''}
              `}
            >
              {isDownloading ? (
                <div className="flex items-center justify-center">
                  <svg className="animate-spin h-5 w-5 mr-3 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"/>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"/>
                  </svg>
                  Generating Document...
                </div>
              ) : (
                <div className="flex items-center justify-center">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4" />
                  </svg>
                  Download Cover Letter
                </div>
              )}
            </button>

            <p className="mt-4 text-sm text-gray-600">
              Your cover letter will be downloaded as a Microsoft Word document (.docx)
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DownloadCoverLetter;
