import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './DownloadCoverLetter.css';
import { htmlToText } from 'html-to-text';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

function DownloadCoverLetter({ selectedSections, coverLetter, setCoverLetter, resumeText }) {
  const [content, setContent] = useState('');
  const [personalDetails, setPersonalDetails] = useState({
    name: '',
    address: '',
    cityStateZip: '',
    email: '',
    phoneNumber: '',
  });
  const currentDate = new Date().toLocaleDateString();
  const requiredSections = ['Open Hook', 'Key Experiences', 'Personal Values', 'Closing Statement'];
  const allSectionsCompleted = requiredSections.every(section => selectedSections[section]);
  const [isDownloading, setIsDownloading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [showSuccessMessage, setShowSuccessMessage] = useState(false);

  useEffect(() => {
    const extractPersonalDetails = () => {
      if (resumeText) {
        // Split text into lines and remove empty lines
        const lines = resumeText.split('\n').map(line => line.trim()).filter(line => line !== '');

        // Extract name (assumed to be the first non-empty line)
        const name = lines.length > 0 ? lines[0] : '[Your Name]';

        // Extract email
        const emailMatch = resumeText.match(/\b[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}\b/i);
        const email = emailMatch ? emailMatch[0] : '[Your Email]';

        // Extract phone number
        const phoneMatch = resumeText.match(/(\+?\d{1,3}[-.\s]?|\()?\d{3}[-.\s]?\d{3}[-.\s]?\d{4}/);
        const phoneNumber = phoneMatch ? phoneMatch[0] : '[Your Phone Number]';

        // Set the extracted details
        setPersonalDetails(prevDetails => ({
          ...prevDetails,
          name,
          email,
          phoneNumber,
          address: '[Your Address]',
          cityStateZip: '[City, State ZIP]'
        }));
      }
    };

    extractPersonalDetails();
  }, [resumeText]);

  useEffect(() => {
    const initialContent = `
${personalDetails.name || '[Your Name]'}<br />
${personalDetails.address || '[Your Address]'}<br />
${personalDetails.cityStateZip || '[City, State ZIP]'}<br />
${personalDetails.email || '[Your Email]'}<br />
${personalDetails.phoneNumber || '[Your Phone Number]'}<br />
${currentDate}<br /><br />

Dear Hiring Manager,<br /><br />
${selectedSections['Open Hook'] || ''}<br /><br />
${selectedSections['Key Experiences'] || ''}<br /><br />
${selectedSections['Personal Values'] || ''}<br /><br />
${selectedSections['Closing Statement'] || ''}<br /><br />

Sincerely,<br />
${personalDetails.name || '[Your Name]'}<br />
    `;
    setContent(initialContent);
    setCoverLetter(initialContent);
  }, [personalDetails, currentDate, selectedSections, setCoverLetter]);

  if (!allSectionsCompleted) {
    return <Navigate to="/cover-letter-section" replace />;
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPersonalDetails(prev => ({ ...prev, [name]: value }));
  };

  // Handle changes in editor
  const handleContentChange = (value) => {
    setContent(value);
    setCoverLetter(value);
  };

  // Toolbar options for the editor
  const toolbarOptions = [
    [{ 'header': '1' }, { 'header': '2' }, { 'font': [] }],
    [{ 'list': 'ordered'}, { 'list': 'bullet' }],
    ['bold', 'italic', 'underline'],
    [{ 'align': [] }],
    ['link'],
    ['clean']                                         
  ];

  const handleDownload = async () => {
    setIsDownloading(true);
    setErrorMessage('');
    try {
      const response = await axios.post(
        'http://localhost:3000/generate-word',
        { coverLetter: content },
        { responseType: 'blob' }
      );

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'CoverLetter.docx');
      document.body.appendChild(link);
      link.click();
      link.parentNode.removeChild(link);

      setShowSuccessMessage(true);
      setTimeout(() => setShowSuccessMessage(false), 3000);
    } catch (error) {
      console.error('Error downloading Word document:', error);
      setErrorMessage('Failed to download the document. Please try again.');
    } finally {
      setIsDownloading(false);
    }
  };



  return (
    <div className="cover-letter-editor p-6 bg-white rounded-lg shadow-md max-w-3xl mx-auto">
      <h2 className="text-2xl font-semibold mb-4 text-center">Letter Content</h2>
      <p className="text-sm text-gray-600 mb-6 text-left">Personal Details</p>

      <div className="personal-details-form grid gap-4 mb-6 md:grid-cols-2">
        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Name</label>
          <input
            type="text"
            name="name"
            value={personalDetails.name}
            onChange={handleInputChange}
            className="bg-gray-50 border border-gray-300 text-gray-800 text-sm rounded-lg focus:ring-blue-400 focus:border-blue-500 block w-full p-2.5"
            placeholder="Lebron James"
          />
        </div>
        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Address</label>
          <input
            type="text"
            name="address"
            value={personalDetails.address}
            onChange={handleInputChange}
            className="bg-gray-50 border border-gray-300 text-gray-800 text-sm rounded-lg focus:ring-blue-400 focus:border-blue-500 block w-full p-2.5"
            placeholder="47 W 13th St, New York, US"
          />
        </div>
        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">City, State, Zip</label>
          <input
            type="text"
            name="cityStateZip"
            value={personalDetails.cityStateZip}
            onChange={handleInputChange}
            className="bg-gray-50 border border-gray-300 text-gray-800 text-sm rounded-lg focus:ring-blue-400 focus:border-blue-500 block w-full p-2.5"
            placeholder="10001"
          />
        </div>
        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Email</label>
          <input
            type="email"
            name="email"
            value={personalDetails.email}
            onChange={handleInputChange}
            className="bg-gray-50 border border-gray-300 text-gray-800 text-sm rounded-lg focus:ring-blue-400 focus:border-blue-500 block w-full p-2.5"
            placeholder="lebron@example.com"
          />
        </div>
        <div>
          <label className="block mb-2 text-sm font-medium text-gray-700">Phone Number</label>
          <input
            type="text"
            name="phoneNumber"
            value={personalDetails.phoneNumber}
            onChange={handleInputChange}
            className="bg-gray-50 border border-gray-300 text-gray-800 text-sm rounded-lg focus:ring-blue-400 focus:border-blue-500 block w-full p-2.5"
            placeholder="+1 123 456 7890"
          />
        </div>
      </div>
      <p className="text-sm text-gray-600 mb-6 text-left">Edit your Cover Letter</p>
      <ReactQuill
        value={content}
        onChange={handleContentChange}
        modules={{ toolbar: toolbarOptions }}
        placeholder="Write your cover letter here..."
        className="mb-6"
      />
      {errorMessage && (
                <p className="text-red-500 text-sm mb-4">{errorMessage}</p>
      )}
      <div className="flex space-x-4">
        <button
            onClick={() => navigator.clipboard.writeText(content)}
            className="flex-1 text-white bg-gray-800 hover:bg-blue-400 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm py-2.5"
        >
            Copy to Clipboard
        </button>
        <button
            onClick={handleDownload}
            disabled={isDownloading}
            className={`flex-1 text-white bg-gray-800 hover:bg-blue-400 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm py-2.5 ${
              isDownloading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
        >
            {isDownloading ? 'Downloading...' : 'Download'}
        </button>
      </div>
    </div>
  );
}

export default DownloadCoverLetter;
